import React from "react";

export default function Textform(props) {
  return (
    <div>
      <div className="container my-4">
        <h3 className="heading">{props.heading}</h3>
        <div className="form-floating">
          <textarea
            className="textArea"
            placeholder="Leave a statement here"
            id="myBox"
          ></textarea>
       
          
        </div>
        <button className="btn btn-primary my-5">Convert uppercase</button>
      </div>
     
    </div>
  );
}
