        <h3 className="heading">{props.heading}-{ text }</h3>
        <div className="form-floating">
          <textarea
            className="textArea"
            placeholder="Leave a statement here"
            id="myBox"
          ></textarea>
       
          
        </div>
        <button className="btn btn-primary my-3">Convert uppercase</button>
      </div>
     
    </div>
  );
}
